export const CHANGE_AUTH = 'CHANGE_AUTH'
export const SET_USER = 'SET_USER'

export const changeAuth = (isAuth) => ({
    type: CHANGE_AUTH,
    payload: {isAuth}
})
export const setUser = (user) => ({
    type: CHANGE_AUTH,
    payload: {user}
})
