import React, { useState } from 'react';
import {check, login, registration} from "../api/userApi";
import {useDispatch} from "react-redux";
import {changeAuth, setUser} from "../store/actions";
import CircularProgress from "@mui/material/CircularProgress";
import {useNavigate} from "react-router-dom";
import {Link} from "@mui/material";

const LoginForm = () => {
    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });
    const navigate = useNavigate();
    const dispatch = useDispatch();


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Дані для входу:', formData);
    };

    const signIn = async () => {
            setLoading(true);
        try {
            const response = await login(formData.email, formData.password);
            check().then((data)=>{
                dispatch(setUser({email: formData.email, password: formData.password}));
                dispatch(changeAuth(true))
                navigate('/posts')
            }).finally(()=>setLoading(false));
        } catch (error) {
            alert(error.response.data.message);
            setLoading(false);

        }
    }

    if (loading) {
        return (
            <div className="flex items-center justify-center p-5 h-screen ">
                <CircularProgress color="success" />
            </div>
        )
    }

    return (
        <div className="border-black border-2 w-1/2  rounded-3xl flex p-9 bg-white  ">
            <form onSubmit={handleSubmit} >
                <h1 className="text-2xl font-semibold">Sign In</h1>
                <div >
                    <label htmlFor="email" className="text-sm">Email address:</label>
                    <br/>
                    <input
                        className="w-full p-3 border-2 border-gray-300 rounded-lg text-black bg-gray-100 focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-300 placeholder-gray-400"                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                        placeholder="Your email address"
                    />
                </div>

                <div>
                    <label htmlFor="password">Password:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                </div>

                <p>Forgot password?</p>

                <div>
                    <button type="submit" onClick={signIn}>Login</button>
                </div>
                <span>
                    Don't have an account? <Link to="/registrarion">Sign up</Link>'
                </span>
            </form>
            <div>
                <h2>Kodix</h2>
                <p>Unlimited traffic, strategic support, and AI-driven upsells</p>
                <a href="#">Learn more</a>
            </div>
        </div>
    );
};

export default LoginForm;
