import React, { useState } from 'react';
import {login, registration} from "../api/userApi";
import {useDispatch} from "react-redux";
import {changeAuth, setUser} from "../store/actions";

const LoginForm = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });

    const dispatch = useDispatch();


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Обробка події входу (наприклад, перевірка даних та відправка запиту на сервер)
        console.log('Дані для входу:', formData);
    };

    const signIn = async () => {
        try {
            const response = await login(formData.email, formData.password);
            dispatch(changeAuth(true))
            dispatch(setUser({email: formData.email, password: formData.password}));
        } catch (error) {
            alert(error.response.data.message);
        }
    }

    return (
        <form onSubmit={handleSubmit} className="bg-green-700 text-black p-1">
            <div >
                <label htmlFor="email">Email:</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
            </div>

            <div>
                <label htmlFor="password">Password:</label>
                <input
                    type="password"
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                />
            </div>

            <div>
                <button type="submit" onClick={signIn}>Login</button>
            </div>
        </form>
    );
};

export default LoginForm;
