import {CHANGE_AUTH, SET_POSTS, SET_USER} from "./actions";

const initialState = {
    isAuthenticated: false,
    user: {

    },
    posts: {
        postPage: 4,
        allPosts: [
            { id: 1, title: 'Post 1', content: 'Content 1', date: 'January 1, 2024', author: 'Author 1' },
        ],
    },
    currentPost: {
        post: {},
        comments: {},
        userPost: {},
    },
}

export const reducers = (state = initialState, action) =>{
    switch(action.type){
        case CHANGE_AUTH:
            return {
                ...state,
                isAuthenticated: !!action.payload.isAuth
            };
            case SET_USER:
                return {
                    ...state,
                    user: action.payload.user
                }
        case SET_POSTS:
            return {
                ...state,
                posts: {
                    ...state.posts,
                    posts: action.payload.posts
                },
            };
        default:
            return state;
    }
}
