import {CHANGE_AUTH} from "./actions";

const initialState = {
    isAuthenticated: true,
    user: {
        email: '',
        password: '',
        firstName: '',
        lastName: '',
    }
}

export const reducers = (state = initialState, action) =>{
    switch(action.type){
        case CHANGE_AUTH:
            return {
                ...state,
                isAuthenticated: !!action.payload.isAuth
            };
        default:
            return state;
    }
}
