import {CHANGE_AUTH} from "./actions";
import {combineReducers, createStore} from "redux";

const initialState = {
    isAuthenticated: true,
}
export const reducer = createStore(initialState, {})
export const reducers = (state = initialState, action) =>{
    switch(action.type){
        case CHANGE_AUTH:
            return {
                ...state,
                isAuthenticated: !!action.payload.isAuth
            };
        default:
            return state;
    }
}
