import {$host} from "./index";

export const registration = async (email, password, first_name, last_name) => {
    return await $host.post('api/user/register', {email, password, first_name, last_name});
}

export const login = async (email, password) => {
    return await $host.post('api/user/login', {email, password});
}
export const check = async () => {
    return await $host.post('api/user/register');
}
