import React from 'react';
import image from '../assets/PostImage.png';
import {useSelector} from "react-redux";
const PreviewPost = () => {
    const post = useSelector(state => state.posts.allPosts[0])

    return (
        <div>
            <h1 className="font-semibold text-3xl">{post.title}</h1>
            <p className="font-normal">{post.body}</p>
            <div className="flex items-center py-2">
                <p className="font-semibold mr-1">wednesday 12, march 2024</p>
                <div className="bg-white border-2  items-center p-1 w-32 rounded-3xl flex">
                    <div className="bg-gray-400 w-10 h-10 rounded-3xl contain-content mr-1">.</div>
                    <p>John Doe</p>
                </div>
            </div>
            <img src={image} alt="post image" />
            <div>
                Share to
            </div>
        </div>
    );
};

export default PreviewPost;
