import React, {useContext, useEffect} from 'react';
import logo from '../assets/logo.svg';
import { useSelector } from 'react-redux';
import {Context} from "../index";

const NavBar = () => {
    const {user} = useContext(Context); // отримуємо значення isAuth із Redux
    console.log(user);

    return (
        <div className="flex items-center justify-between p-4">
            <nav className="text-center">
                <ul className="flex justify-center space-x-8">
                    <li className="text-sm font-semibold hover:text-blue-500"><a href="#">Home</a></li>
                    <li className="text-sm font-semibold hover:text-blue-500"><a href="#">Feature</a></li>
                    <li className="text-sm font-semibold hover:text-blue-500"><a href="#">Blog</a></li>
                    <li className="text-sm font-semibold hover:text-blue-500"><a href="#">Testimonials</a></li>
                </ul>
            </nav>
            <img src={logo} alt="logo" className="h-8" />
            <div className="flex space-x-4">
                {/* Перевіряємо, чи isAuth є true або false */}
                {user ? (
                    <>
                        <button className="px-12 py-2 bg-green-700 text-white rounded-3xl hover:bg-green-700">
                            Login
                        </button>
                        <button className="px-12 py-2 border border-green-700 text-green-500 rounded-3xl hover:bg-green-700 hover:text-white">
                            Sign Up
                        </button>
                    </>
                ) : (
                    <button className="px-12 py-2 border border-green-700 text-green-500 rounded-3xl hover:bg-green-700 hover:text-white">
                        Account
                    </button>
                )}
            </div>
        </div>
    );
};

export default NavBar;
