import React, {useEffect, useState} from 'react';
import image from '../assets/BigImage.png'
import {fetchPosts} from "../api/postApi";
import {setPosts} from "../store/actions";
import {useDispatch} from "react-redux";
import PostCard from "./PostCard";
import { Pagination } from "@mui/material";


const Post = () => {
    const [page, setPage] = useState(0);
    const [currentPosts, setCurrentPosts] = useState([]);

    const dispatch = useDispatch();


    useEffect(() => {
        fetchPosts(1).then((data) => {
            dispatch(setPosts(data.posts));
            setCurrentPosts(data.posts);
        })
    },[])

    return (
        <div>
            <p>Featured</p>
            <h1>Global Climate Summit Urges Immediate Action</h1>
            <p>Leaders from around the world gathered for a global climate summit, emphasizing the urgent need for coordinated action to address climate change.</p>
            <div>
                <p>wednesday 12, march 2024</p>
                <div>
                    <div></div>
                    <p>John Doe</p>
                </div>
            </div>
            <img src={image} alt="bigImage" />
            <div>
                Share to
            </div>
            <div>
                Comments
            </div>
            <div className="flex justify-center">

            {
                currentPosts.map((post) => (
                        <PostCard key={post.id} post={post} vertical={false} />
                ))
            }
            </div>
            <div className="flex justify-center">
                <Pagination
                    count={20}
                    page={page}
                    // onChange={(_, newPage) => {
                    //     window.scrollTo(0, 0)
                    //     setCurentPostsThunk(newPage)
                    // }}
                    variant="outlined"
                    shape="rounded"
                />
            </div>
        </div>
    );
};

export default Post;
