import React, {useEffect, useState} from 'react';
import image from '../assets/BigImage.png'
import {fetchOnePost, fetchPosts} from "../api/postApi";
import {setOnePosts, setPosts} from "../store/actions";
import {useDispatch} from "react-redux";
import PostCard from "./PostCard";
import { Pagination } from "@mui/material";
import {data, useParams} from "react-router-dom";
import CircularProgress from "@mui/material/CircularProgress";
import Comments from "./Comments";


const Post = () => {
    const [post, setPost] = useState({
        post: {
            data:{}
        },
        user: {
            data: [{
                name:'username',
            }]
        },
        comments: [],
    });
    const params = useParams();
    const [loading, setLoading] = useState(false);


    const dispatch = useDispatch();


    useEffect(() => {
        setLoading(true);
        fetchOnePost(params.Id).then((data) => {
            dispatch(setOnePosts(data.post, data.user, data.comments));
            setPost(data);
            setLoading(false);
        })
    },[])

    if (loading) {
        return (
            <div className="flex items-center justify-center p-5 h-screen ">
                <CircularProgress color="success" />
            </div>
        )
    }
    return (
        <div>
            <p>Featured</p>
            {
                console.log(post.user)
            }
            <h1>{post.post.data.title}</h1>
            <p>{post.post.data.body}</p>
            <div>
                <p>wednesday 12, march 2024</p>
                <div>
                    <div></div>
                    <p>{post.user.data[0].name}</p>
                </div>
            </div>
            <img src={image} alt="bigImage" />
            <div>
                Share to
            </div>
            <div>
                {
                    post.comments.data((comment) => (
                        <Comments comment={comment} />
                    ))
                }
            </div>

        </div>
    );
};

export default Post;
