import {CHANGE_AUTH, SET_USER} from "./actions";

const initialState = {
    isAuthenticated: false,
    user: {

    },
    posts: {
        postPage: 4,
        post: {},
    },
}

export const reducers = (state = initialState, action) =>{
    switch(action.type){
        case CHANGE_AUTH:
            return {
                ...state,
                isAuthenticated: !!action.payload.isAuth
            };
            case SET_USER:
                return {
                    ...state,
                    user: action.payload.user
                }
        default:
            return state;
    }
}
