import React, {useEffect, useState} from 'react';
import PostCard from "./PostCard";
import {fetchPosts} from "../api/postApi";
import {useDispatch, useSelector} from "react-redux";
import {addPosts, setPosts} from "../store/actions";

const PostList = () => {
    const [currentPosts, setCurrentPosts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);

    const dispatch = useDispatch();

    const showMore = () =>{
        let page = currentPage + 1;
        fetchPosts(page).then((data) => {
            debugger

            dispatch(addPosts(data.posts));
            setCurrentPosts(prevPosts => [
                ...prevPosts,
                ...data.posts
            ]);

        })
    }
    useEffect(() => {
        fetchPosts(1).then((data) => {
            dispatch(setPosts(data.posts));
            setCurrentPosts(data.posts);
        })
    },[])
    return (
        <div className="p-3">
            <div className="flex justify-between gap-4 mb-8">
                    <h2>Related Articles</h2>
                    <button
                        className="font-semibold px-12 py-2 border border-balck-700 text-green-500 rounded-3xl hover:bg-green-700 hover:text-white">
                    Read More</button>
            </div>
            {
                currentPosts.map((post) => (
                    <PostCard key={post.id} post={post} vertical={true} />
                ))
            }
            <div className="flex justify-center cursor-pointer" onClick={showMore}>Show more</div>
        </div>
    );
};

export default PostList;
