import {CHANGE_AUTH, SET_USER} from "./actions";

const initialState = {
    isAuthenticated: false,
    user: {

    },
    posts: {
        postPage: 4,
        allPosts: [],
    },
    currentPost: {
        post: {},
        comments: {},
        userPost: {},
    },
}

export const reducers = (state = initialState, action) =>{
    switch(action.type){
        case CHANGE_AUTH:
            return {
                ...state,
                isAuthenticated: !!action.payload.isAuth
            };
            case SET_USER:
                return {
                    ...state,
                    user: action.payload.user
                }
        case SET_POSTS:
            // startIndex =
            //     (action.payload.posts.length / 10) * (state.posts.postPage - 1);

            return {
                ...state,
                posts: {
                    ...state.posts,
                    posts: action.payload.posts
                },
            };
        default:
            return state;
    }
}
