import React from 'react';
import image from '../assets/PostImage.png';
import {useSelector} from "react-redux";
import {useNavigate} from "react-router-dom";
import {POST_ROUTE, POSTS_ROUTE} from "../utils/consts";
const PreviewPost = () => {
    const post = useSelector(state => state.posts.allPosts[0])
    const navigate = useNavigate();

    return (
        <div className="mt-8 cursor-pointer" onClick={() => navigate(POST_ROUTE + post.id)}>
            <h1 className="font-semibold text-3xl mb-2">{post.title}</h1>
            <p className="font-normal mb-6">{post.body}</p>
            <div className="flex items-center py-2">
                <p className="font-semibold mr-1">wednesday 12, march 2024</p>
                <div className=" mb-5 bg-white border-2  items-center p-1 w-32 rounded-3xl flex">
                    <div className="bg-gray-400 w-10 h-10 rounded-3xl contain-content mr-1">.</div>
                    <p>John Doe</p>
                </div>
            </div>
            <img className="mb-5" src={image} alt="post image" />
            <div>
                Share to
            </div>
        </div>
    );
};

export default PreviewPost;
