import React, { useState } from 'react';
import {$host} from "../api";
import {check, registration} from "../api/userApi";
import {useDispatch, useSelector} from "react-redux";
import {changeAuth, setUser} from "../store/actions";
import {useNavigate} from "react-router-dom";

const RegistrationForm = () => {
    const [loading, setLoading] = useState(false);

    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: '',
    });
    const navigate = useNavigate();
    
    const dispatch = useDispatch();
    const isAuth = useSelector(state => state.isAuthenticated);
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Форма надіслана:', formData);
    };

    const signUp = async () => {
        setLoading(true);

        try {
            const response = await registration(formData.email, formData.password, formData.email, formData.username);
            check().then((data)=>{
                dispatch(setUser(formData))
                dispatch(changeAuth(true))
                navigate('/posts')

            }).finally(()=>setLoading(false));
        } catch (error) {
            alert(error.response.data.message);
            setLoading(false);

        }
    }

    return (
        <form onSubmit={handleSubmit} className="bg-green-700 text-black p-1">
            <div >
                <label htmlFor="username">Username:</label>
                <input
                    type="text"
                    id="username"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    required
                />
            </div>

            <div>
                <label htmlFor="email">Email:</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
            </div>

            <div>
                <label htmlFor="password">Password:</label>
                <input
                    className="bg-amber-300 text-blue-700"
                    // type="password"
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                />
            </div>

            <div>
                <label htmlFor="confirmPassword">Confirm Password:</label>
                <input
                    type="password"
                    id="confirmPassword"
                    name="confirmPassword"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                    required
                />
            </div>

            <div>
                <button type="submit" onClick={signUp}>Register</button>
            </div>
        </form>
    );
};

export default RegistrationForm;
