import React, {useEffect, useState} from 'react';
import image from '../assets/BigImage.png'
import {fetchOnePost, fetchPosts} from "../api/postApi";
import {setOnePosts, setPosts} from "../store/actions";
import {useDispatch} from "react-redux";
import PostCard from "./PostCard";
import { Pagination } from "@mui/material";
import {useParams} from "react-router-dom";


const Post = () => {
    const [post, setPost] = useState({});
    const {id} = useParams();

    const dispatch = useDispatch();


            debugger
    useEffect((id) => {
        fetchOnePost().then((data) => {
            dispatch(setOnePosts(data.post, data.user, data.comments));
            setPost(data);
        })
    },[])
    return (
        <div>
            <p>Featured</p>
            <h1>{post.post.title}</h1>
            <p>{post.post.body}</p>
            <div>
                <p>wednesday 12, march 2024</p>
                <div>
                    <div></div>
                    <p>{post.user.name}</p>
                </div>
            </div>
            <img src={image} alt="bigImage" />
            <div>
                Share to
            </div>
            <div>
                Comments
            </div>

        </div>
    );
};

export default Post;
