import React from 'react';
import PostImage from "../customComponents/PostImage";
import {useNavigate} from "react-router-dom";

const PostCard = ({vertical, post}) => {
    const navigate = useNavigate();
    function truncateText(text) {
        if (text.length > 34) {
        console.log(text);
            return text.slice(0, 34) + '...';

        }
        return text;
    }
    return (
        <div className={vertical ? "flex":"w-full mb-8"}
             onClick={() => navigate(`/post/${post.id}`)}
        >
            <PostImage />
            <div className='w-1/2 '>
                <p className="uppercase">wednesday 12, march 2024</p>
                <h2 className="text-md font-semibold">{truncateText(post.title)}</h2>
                <p>{post.body}</p>
            </div>
        </div>
    );
};

export default PostCard;
